const rawOutput = $input.first().json.output;

try {
  const main = JSON.parse(rawOutput.trim());
  const payload = JSON.parse(main.payload);
  return [{
    json: {
      type: main.type,
      url: main.url,
      payload: payload
    }
  }];
} catch (error) {
  return [{ json: { error: error.message, raw: rawOutput } }];
}
